package mk.finki.emt.retailacar.config;

import lombok.AllArgsConstructor;
import mk.finki.emt.retailacar.domain.models.Car;
import mk.finki.emt.retailacar.domain.repository.CarRepository;
import mk.ukim.finki.emt.sharedkernel.domain.financial.Currency;
import mk.ukim.finki.emt.sharedkernel.domain.financial.Money;
import org.springframework.stereotype.Component;

import javax.annotation.PostConstruct;
import java.util.Arrays;

@Component
@AllArgsConstructor
public class DataInitializer {

    private final CarRepository carRepository;

    @PostConstruct
    public void initData() {
        Car car1 = Car.build("Audi", "green", 10, Money.valueOf(Currency.MKD, 2000.0),
                null, "https://monaco-cars.rent/img/cars-img/audi--q7-50-tdi-quattro-white/audi--q7-50-tdi-quattro-white--bb0ebe571ccc4878f080e64fbb416be0--1280x960.jpg");
        Car car2 = Car.build("Ferrari", "red", 5, Money.valueOf(Currency.MKD, 2500.0), null, "data:image/jpeg;base64,");
        if (carRepository.findAll().isEmpty())
            carRepository.saveAll(Arrays.asList(car1, car2));

    }
}
